$(function(){
  $('.product-slider__items').slick({
    prevArrow: '<button class="slick-btn slick-prev lnr lnr-chevron-left"></button>',
    nextArrow: '<button class="slick-btn slick-next slick-arrow lnr lnr-chevron-right"></button>'
  });
  $('.followers__items').slick({
    prevArrow: '<button class="slick-btn slick-prev lnr lnr-chevron-left"></button>',
    nextArrow: '<button class="slick-btn slick-next slick-arrow lnr lnr-chevron-right"></button>',
    slidesToShow: 3,
    slidesToScroll: 3
  })

  $(".rateYo").rateYo({
    rating: 4.5,
    readOnly: true,
    starWidth: "12px"
  });
  $(".header__bottom-nav__btn").on('click', function () {
    $(".header__bottom-nav").slideToggle()
  })
  $(".header__profile-btn").on('click', function () {
    $(".header__profile").toggleClass("active")
  })

  $('select, input').styler();

  $("#aside-price").ionRangeSlider({
    type: "double",
    grid: false,
    min: 0,
    max: 1000,
    from: 30,
    to: 600,
    prefix: "$",
    skin: "round",
    hide_min_max: true        
});

  $('.top-select__btn-list').on('click', function(){
    $('.product__item').addClass('product__item--list')
  });
  $('.top-select__btn-grid').on('click', function(){
    $('.product__item').removeClass('product__item--list')
  });

  const btns = document.querySelectorAll(".tabs__button");
  const tabContent = document.querySelectorAll(".tab-content");
  
  for (let i = 0; i < btns.length; i++) {
    btns[i].addEventListener("click", () => {
      addClassFunc(btns[i], "tabs__button--active");
      clearClassFunc(i, btns, "tabs__button--active");
  
      addClassFunc(tabContent[i], "tab-content--active");
      clearClassFunc(i, tabContent, "tab-content--active");
    });
  }
  
  function addClassFunc(elem, elemClass) {
    elem.classList.add(elemClass);
  }
  
  function clearClassFunc(indx, elems, elemClass) {
    for (let i = 0; i < elems.length; i++) {
      if (i === indx) {
        continue;
      }
      elems[i].classList.remove(elemClass);
    }
  }


  var mixer = mixitup('.product__items, .product-items, .blog__items');
})